import './App.css';
import { BrowserRouter, Routes, Route, Navigate } from 'react-router-dom';
import Home from './pages/Home/Home';
import { useSelector } from 'react-redux';
import Navigation from './components/shared/Navigation/Navigation';
import Register from './pages/Register/Register';
import Login from './pages/Login/Login';
import Authenticate from './pages/Authenticate/Authenticate';
import Activate from './pages/Activate/Activate';
const isAuth = true;
const user = {
  activated:true
}

function GuestAuth({ children }) {
  // let auth = useAuth();
  // let location = useLocation();

  if (isAuth) {
    // Redirect them to the /private rooms page, but save the current location they were
    // trying to go to when they were redirected. This allows us to send them
    // than dropping them off on the home page.
    //state={{ from: location }} 
    return <Navigate to="/rooms" />;
  }

  return children;
}

function App() {
  return (
    <BrowserRouter>
      <Navigation />
      <Routes>
        {/* <Route path="/" element={<Home />} />
        <Route path="/register" element={<Register />} />
        <Route path="/login" element={<Login />} /> */}
        <Route path='/authenticate' element={<GuestAuth>
          <Authenticate />
          </GuestAuth>} />

        <Route path='/activate' element={<SemiProtectedRoute>
          <Activate />
        </SemiProtectedRoute>}/>
      </Routes>
    </BrowserRouter>
  );
}

function SemiProtectedRoute({children, ...rest}) {
  return (
    <Route
        {...rest}
        render={({ location }) => {
            return !isAuth ? (
                <Navigate
                    to={{
                        pathname: '/',
                        state: { from: location },
                    }}
                />
            ) : isAuth && !user.activated ? (
                children
            ) : (
                <Navigate
                    to={{
                        pathname: '/rooms',
                        state: { from: location },
                    }}
                />
            );
        }}
    ></Route>
);

}

export default App;
